    try {
      let { data } = await axiosRequest.get(`${API}/Product/get-products`, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      });
      setData(data.data.products);
      console.log(data);
    } catch (error) {
      console.error(error);
    }
